import Header from "../components/headers/Header";
import Button from "../components/buttons/Button";
import {useNavigate} from "react-router-dom";
import {useEffect, useState} from "react";
import axios from "../config/axios";
import FeedItem from "../components/items/FeedItem";

const Feed = () => {

    const navigate = useNavigate();

    const [posts, setPosts] = useState([]);

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalPages, setTotalPages] = useState(0);

    const [fetching, setFetching] = useState(true);

    const [isAdmin, setIsAdmin] = useState(false);
    const [isModerator, setIsModerator] = useState(false);
    const [isAnonymous, setIsAnonymous] = useState(true);


    const goToCourses = () => {
        navigate('/courses');
    }

    const scrollHandler = (e) => {
        if (e.target.documentElement.scrollHeight - (e.target.documentElement.scrollTop + window.innerHeight) < 100
            && totalPages === posts.length) {
            setFetching(true);
        }
    }

    useEffect(() => {
        if (JSON.parse(sessionStorage.getItem('user')) === null) {
            setIsAdmin(false);
            setIsModerator(false);
            setIsAnonymous(true);
        } else {
            if (JSON.parse(sessionStorage.getItem('user')).role === 'Администратор') {
                setIsAdmin(true);
                setIsAnonymous(false);
            } else if (JSON.parse(sessionStorage.getItem('user')).role === 'Модератор') {
                setIsModerator(true);
                setIsAnonymous(false);
            }
        }

    }, []);

    useEffect(() => {
        if (fetching) {
            axios
                .get('/news_publications',
                    {
                        params: {
                            pageNumber: pageNumber - 1,
                            pageSize: pageSize,
                        },
                        headers: {
                            "Access-Control-Allow-Origin": "*"
                        },
                        withCredentials: true
                    }
                )
                .then(response => {
                    setPosts([...posts, ...response.data.content]);
                    setPageNumber(prevState => prevState + 1);
                    setTotalPages(response.data.total_pages);
                })
                .finally(() => setFetching(false));
        }
    }, [fetching]);

    useEffect(() => {
        document.addEventListener('scroll', scrollHandler);

        return function () {
            document.removeEventListener('scroll', scrollHandler);
        }
    }, []);

    return (
        <div>
            <Header/>
            <div className="flex-col">
                {
                    (isAdmin || isModerator) && !isAnonymous &&
                    <div>
                        <Button text="Создать новость"/>
                    </div>
                }
                <div className="ml-[85%]">
                <Button text="Найти нужный материал" handleButton={goToCourses}/>
                </div>
            </div>
            <div>
                {
                    posts.map((post) => {
                        return (
                            <FeedItem description={post.description}
                                      createdAt={post.created_at}
                                      newsId={post.id}
                                      newsTitle={post.title}
                                      files={post.files}
                            />
                        )
                    })
                }
            </div>
        </div>
    )
}

export default Feed;
