import {useParams} from "react-router-dom";
import Header from "../components/headers/Header";
import Button from "../components/buttons/Button";
import {useState} from "react";
import ProfileItem from "../components/items/ProfileItem";

const Profile = () => {
    const {nickname} = useParams();
    const [profilePhoto, setProfilePhoto] = useState();
    const [name, setName] = useState('Андрей');
    const [lastName, setLastName] = useState('Пряничников');
    const [middleName, setMiddleName] = useState('Алексеевич');
    const [updatingNickname, setUpdatingNickname] = useState(nickname);
    const [email, setEmail] = useState('kalinfifa@mail.ru');
    const [birthdate, setBirthdate] = useState('');

    const [editMode, setEditMode] = useState(false);

    const handleProfilePhoto = (e) => {
        let newPhoto = e.target.files;
        const validExtension = ['jpg', 'png'];
        const maxSize = 5 * 1024 * 1024;
        const fileExtension = newPhoto[0].name.split('.').pop().toLowerCase();

        let valid = validExtension.includes(fileExtension);
        let validSize = !(newPhoto.size > maxSize);

        if (!valid) {
            alert(`"Файл ${newPhoto[0].name} имеет неверное расширение. Допустимые расширения: ${validExtension.join(', ')}"`);
        }

        if (!validSize) {
            alert(`"Файл ${newPhoto.name} превышает допустимый размер (5 MB)."`);
        }

        if (valid && validSize) {
            setProfilePhoto(URL.createObjectURL(newPhoto[0]));
        }


    }

    const handleEditMode = () => {
        setEditMode(true);
    }

    const updateInfo = () => {
        setEditMode(false);
        // todo
    }

    return (
        <div>
            <Header/>
            <div className="h-[32vh] flex">
                <div className="w-[20vh] bg-gray-300 rounded-2xl justify-center">
                    <div className="flex mb-auto bg-black m-2">
                        <img src={profilePhoto} width="200" height="200"/>
                    </div>
                    {
                        editMode &&
                        <div className="flex justify-center mt-2">
                            <input type="file" onChange={e => handleProfilePhoto(e)}/>
                        </div>
                    }
                </div>
                <div className="flex-сol bg-gray-300 rounded-2xl w-[100%] ml-2 mr-2">
                    <div className="ml-3">
                        <div className="flex h-[5vh] text-2xl">
                            Данные профиля
                        </div>

                        <ProfileItem title="Фамилия"
                                     editMode={editMode}
                                     content={lastName}
                                     setNewValue={setLastName}
                        />

                        <ProfileItem title="Имя"
                                     editMode={editMode}
                                     content={name}
                                     setNewValue={setName()}
                        />

                        <div className="flex">
                            <div className="mr-3">Имя</div>
                            {!editMode &&
                                <div className="flex">
                                    <div>
                                        {name}
                                    </div>
                                </div>
                            }
                            {editMode &&
                                <div className="flex">
                                    <input placeholder="Имя" type="text" value={name}
                                           onChange={event => setName(event.target.value)}/>
                                </div>
                            }
                        </div>

                        <div className="flex">
                            <div className="mr-3">Отчество</div>
                            {!editMode &&
                                <div className="flex">
                                    <div>
                                        {middleName}
                                    </div>
                                </div>
                            }
                            {editMode &&
                                <div className="flex">
                                    <input placeholder="Отчество" type="text" value={middleName}
                                           onChange={event => setMiddleName(event.target.value)}/>
                                </div>
                            }
                        </div>


                        <div className="flex">
                            <div className="mr-3">Почта</div>
                            {!editMode &&
                                <div className="flex">
                                    <div>
                                        {email}
                                    </div>
                                </div>
                            }
                            {editMode &&
                                <div className="flex">
                                    <input placeholder="Почта" type="text" value={email}
                                           onChange={event => setEmail(event.target.value)}/>
                                </div>
                            }
                        </div>

                        <div className="flex">
                            <div className="mr-3">Никнейм</div>
                            {!editMode &&
                                <div className="flex">
                                    <div>
                                        {updatingNickname}
                                    </div>
                                </div>
                            }
                            {editMode &&
                                <div className="flex">
                                    <input placeholder="Никнейм" type="text" value={updatingNickname}
                                           onChange={event => setUpdatingNickname(event.target.value)}/>
                                </div>
                            }
                        </div>


                        <div className="flex">
                            <div>
                                Роль - {JSON.parse(sessionStorage.getItem('user')).role}
                            </div>
                        </div>

                        <div className="flex">
                            <label>
                                Дата рождения:
                                {!editMode && <div>{birthdate}</div>}
                                {editMode &&
                                    <input
                                        type="date"
                                        value={birthdate}
                                        className="ml-3"
                                        onChange={(e) => setBirthdate(e.target.value)}
                                    />
                                }
                            </label>
                        </div>

                        {!editMode &&
                            <Button text="Редактировать профиль" handleButton={handleEditMode}/>
                        }

                        {editMode &&
                            <Button text="Сохранить изменения" handleButton={updateInfo}/>
                        }
                    </div>

                </div>
            </div>
            <div className="h-[50vh]">
                admin panel
            </div>
        </div>
    )
}

export default Profile;
