import Button from "../buttons/Button";
import "./Form.css"
import {useState} from "react";

const Form = () => {

    const [subjectTitle, setSubjectTitle] = useState('');

    const createNewSubject = (event) => {
        event.preventDefault();
        console.log(event.target.value);
        console.log(document.getElementById("subjectName"));
    }

    return (
        <div className="form min-w-[900px] max-w-[80vw] mx-auto flex justify-center">
            <form onSubmit={createNewSubject}>
                <label>
                    <input className="border-2 border-purple-300" type="text" placeholder="Название продукта" id="subjectName" value={subjectTitle} onChange={e => setSubjectTitle()}/>
                </label>
                <Button text="Создать предмет" handleButton={createNewSubject}/>
            </form>
        </div>
    )
}

export default Form;
