import {useNavigate} from "react-router-dom";

const PublicationItemList = ({publications, selectedPublication, setSelectedPublication}) => {

    const navigate = useNavigate();

    const handlePublication = (event) => {
        setSelectedPublication(event.target.value);
        console.log(selectedPublication);
    }

    return (
        <div className="flex flex-col flex-nowrap items-center w-[90%] m-auto">
            {
                publications.map((publication) => {
                    return (
                        <div key={publication.id}
                             className="cursor-pointer mb-4 rounded-[1px] bg-red-400 w-[80vw] text-center"
                             onClick={event => handlePublication(event)}
                        >
                            {publication.description}
                        </div>
                    );
                })
            }
        </div>
    )

}

export default PublicationItemList;
