import {useEffect} from "react";
import axios from "axios";
import Header from "../components/headers/Header";
import Publication from "../components/items/Publication";

const url = axios.create({
    baseURL: 'http://localhost:8080/v1/'
});

const Publications = ({subjectTopicId, publications, setPublications}) => {
    useEffect(() => {
        const getPublications = () => {
            return url
                .get('/publications', {params: {subjectTopicId: subjectTopicId, pageNumber: 0, pageSize: 50}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        }

        const get_data = async () => {
            let publications = await getPublications();

            let list = [];
            for (let i = 0; i < publications.content.length; i++) {
                let id = publications.content[i].id;
                let description = publications.content[i].description;
                list.push(
                    {
                        id: id,
                        description: description
                    }
                );
            }

            setPublications(list);
            console.log(list);
        }

        get_data().catch(console.error);

    }, [setPublications]);

    return (
        <div>
            <Header/>
            <div className="flex flex-col flex-nowrap max-w-[95%] ">
                <Publication publicationId="1" publicationDesc="Описание"/>
                <Publication publicationId="1" publicationDesc="Описание"/>
                <Publication publicationId="1" publicationDesc="Описание"/>
                <Publication publicationId="1" publicationDesc="Описание"/>
            </div>

        </div>
    );

}

export default Publications;
