import Header from "../components/headers/Header";
import Footer from "../components/footers/Footer";
import Selector from "../components/selects/Selector";
import {useEffect, useState} from "react";
import axios from "axios";
import Button from "../components/buttons/Button";

const url = axios.create({
    baseURL: 'http://localhost:8080/v1/',
});

const Course = (props) => {

    useEffect(() => {

        const get_courses = () => {
            return url
                .get('/courses')
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                });
        }

        const get_data = async () => {
            let courses = await get_courses();

            let list = [];
            for (let i = 0; i < courses.length; i++) {
                let course_number = courses[i].course_number;
                let desc = courses[i].description;
                list.push(
                    {
                        course_number: course_number,
                        description: desc
                    }
                    );
            }

            props.setCourses(list);
        }

        get_data().catch(console.error);

    }, []);

    return (
        <div>
            <Header title={props.title}/>
            <Selector courses={props.courses} selectedCourse={props.selectedCourse} onSelectChange={props.handleSelectCourse}/>
            <Button text="Показать предметы"/>
            <Footer note={props.note}/>
        </div>
    );
}

export default Course;
