import Button from "../buttons/Button";
import "./Form.css"
import {useState} from "react";
import axios from "axios";

const SubjectForm = ({course, subjectId}) => {

    const [topicTitle, setTopicTitle] = useState('');

    const url = axios.create({
        baseURL: 'http://localhost:8080/v1/',
    });

    const handleTopicTitle = (event) => {
        event.preventDefault();
        setTopicTitle(event.target.value);
    }

    const createNewSubject = (event) => {
        event.preventDefault();
        console.log(topicTitle);

        return url
            .post('/subject-topics', {course_number : course, subject_id : subjectId, topic_id: 1})
            .then((response) => {
                return response.data;
            })
            .catch((error) => {
                return error;
            });

    }

    return (
        <div className="form min-w-[900px] max-w-[80vw] mx-auto flex justify-center">
            <form>
                <label>
                    <input className="border-2 border-purple-300" type="text" placeholder="Название продукта"
                           id="subjectName" value={topicTitle} onChange={e => setTopicTitle(e)}/>
                </label>
                <Button text="Создать предмет" handleButton={createNewSubject}/>
            </form>
        </div>
    )
}

export default SubjectForm;
