import {useParams} from "react-router-dom";
import Header from "../components/headers/Header";
import Button from "../components/buttons/Button";
import {useState} from "react";
const Profile = () => {
    const {nickname} = useParams();
    const [profilePhoto, setProfilePhoto] = useState();

    const handleProfilePhoto = (e) => {
        let newPhoto = e.target.files;
        const validExtension = ['jpg', 'png'];
        const maxSize = 5 * 1024 * 1024;
        // const fileExtension = newPhoto.name.split('.').pop().toLowerCase();
        //
        // if (!validExtension.includes(fileExtension)) {
        //     alert(`"Файл ${newPhoto.name} имеет неверное расширение. Допустимые расширения: ${validExtension.join(', ')}"`);
        // }

        if (newPhoto.size > maxSize) {
            alert(`"Файл ${newPhoto.name} превышает допустимый размер (5 MB)."`);
        }

        setProfilePhoto(URL.createObjectURL(newPhoto[0]));

    }

    return (
        <div>
            <Header/>
            <div className="h-[60vh] flex">
                <div className="w-[20vh] bg-gray-300 rounded-2xl justify-center">
                    <div className="flex mb-auto bg-black m-2">
                        {/*<img src={require('../p_MG_4687.jpg')} width="200" height="200"/>*/}
                        <img src={profilePhoto} width="200" height="200"/>
                    </div>
                    <div className="flex justify-center mt-2">
                        <input type="file" onChange={e => handleProfilePhoto(e)}/>
                    </div>
                </div>
                <div className="m-auto">
                    other info
                </div>
            </div>
            <div className="h-[50vh]">
                admin panel
            </div>
        </div>
    )
}

export default Profile;
