import {useEffect, useState} from "react";
import axios from "../config/axios";
import Header from "../components/headers/Header";
import SubjectItemList from "../components/items/SubjectItemList";
import SubjectForm from "../components/form/SubjectForm";

const Subject = ({course, subjects, setSubjects, selectedSubject, setSelectedSubject}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [pageCount, setPageCount] = useState(0);

    useEffect(() => {
        const getSubjects = () => {
            return axios
                .get('/subjects', {params: {pageNumber: pageNumber - 1, pageSize: pageSize, course: course}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                });
        }

        const getData = async () => {
            let subjects = await getSubjects();

            let list = [];
            for (let i = 0; i < subjects.length; i++) {
                let id = subjects[i].id;
                let name = subjects[i].name;

                list.push(
                    {
                        id: id,
                        name: name
                    }
                );
            }
            setSubjects(list);
            console.log(subjects);
        }
        getData().catch(console.error);

    }, [course, pageNumber, pageSize])

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <SubjectItemList subjects={subjects} selectedSubject={selectedSubject}
                                 setSelectedSubject={setSelectedSubject}/>

                <div className="w-[900px] w-[80vw] mx-auto flex justify-center mb-3">
                    <a href="#"
                       className="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">

                        <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Test</h5>
                        <p className="font-normal text-gray-700 dark:text-gray-400">text</p>
                    </a>
                </div>

                <SubjectForm/>

            </div>
            {/*<Footer/>*/}
        </div>
    )
}

export default Subject;
