import {useEffect} from "react";
import axios from "axios";
import Header from "../components/headers/Header";
import Publication from "../components/items/Publication";

const url = axios.create({
    baseURL: 'http://localhost:8080/v1/'
});

const Publications = ({subjectTopicId, publications, setPublications}) => {
    useEffect(() => {
        const getPublications = () => {
            return url
                .get('/publications', {params: {subjectTopicId: subjectTopicId, pageNumber: 0, pageSize: 50}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        }

        const get_data = async () => {
            let publications = await getPublications();

            let list = [];
            for (let i = 0; i < publications.content.length; i++) {
                let id = publications.content[i].id;
                let description = publications.content[i].description;
                list.push(
                    {
                        id: id,
                        description: description
                    }
                );
            }

            setPublications(list);
            console.log(list);
        }

        get_data().catch(console.error);

    }, [setPublications]);

    return (
        <div>
            <Header/>
            <div className="fixed inset-0 overflow-y-auto">
                <div className="flex items-center justify-center min-h-screen bg-black bg-opacity-70">
                    <div className="bg-white p-8 rounded-lg shadow-md h-[80vh] w-[120vh] overflow-auto">

                        <h1 className="text-2xl">example</h1>
                        <div className="max-h-[75%] overflow-auto">
                            <span className="text-lg pr-5 text-justify">da</span>
                        </div>

                        <br>
                            <hr className="border-amber-300 border-2 mt-2 mb-5">
                                <div className="flex justify-between">
                                    <h2>Комментарии</h2>
                                </div>
                            </hr>
                        </br>
                    </div>
                </div>
            </div>
        </div>
    );

}

export default Publications;
