import {useEffect, useState} from "react";
import axios from "../config/axios";
import Header from "../components/headers/Header";
import PublicationItemList from "../components/items/PublicationItemList";
import {Pagination} from "@mui/material";
import Button from "../components/buttons/Button";
import Modal from "../components/modal/Modal";


const Publications = ({subjectTopicId, publications, setPublications, selectedPublication, setSelectedPublication}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [totalPages, setTotalPages] = useState(1);

    const [open, setOpen] = useState(false);

    const handleButton = () => {
        setOpen(true);
    }

    useEffect(() => {
        const getPublications = () => {
            return axios
                .get('/publications', {
                    params: {
                        subjectTopicId: subjectTopicId,
                        pageNumber: pageNumber - 1,
                        pageSize: pageSize
                    }
                })
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        }

        const getData = async () => {
            let publications = await getPublications();
            let remainder = publications.totalPages % pageSize;
            let extra = 0;
            if (remainder !== 0 && publications.totalPages !== 1) {
                extra = 1;
            }

            setTotalPages(Math.ceil(publications.totalPages / pageSize) + extra);

            let list = [];
            for (let i = 0; i < publications.content.length; i++) {
                console.log(publications.content[i]);
                let id = publications.content[i].id;
                let description = publications.content[i].title;
                list.push(
                    {
                        id: id,
                        title: description
                    }
                );
            }

            setPublications(list);
            console.log(list);
        }

        getData().catch(console.error);

    }, [pageNumber, pageSize]);

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <PublicationItemList publications={publications}
                                     selectedPublication={selectedPublication}
                                     setSelectedPublication={setSelectedPublication}
                />

                <Modal open={open} onClose={() => setOpen(false)}>
                    <div className="text-center w-56">
                        <div className="mx-auto my-4 w-48">
                            <h3 className="text-lg font-black text-gray-800">Добавить новый пост</h3>
                            <p className="text-sm text-gray-500">
                                Are you sure you want to delete this item?
                            </p>
                        </div>
                        <div className="flex gap-4">
                            <button className="btn btn-danger w-full">Delete</button>
                            <button
                                className="btn btn-light w-full"
                                onClick={() => setOpen(false)}
                            >
                                Cancel
                            </button>
                        </div>
                    </div>
                </Modal>

                <Button text="Создать новую публикацию" handleButton={handleButton}/>


                {!!totalPages &&
                    <Pagination
                        sx={{
                            display: 'flex',
                            justifyContent: 'center',
                            marginX: 'auto',
                        }}
                        style={{
                            width: "100%",
                            position: "absolute",
                            bottom: "5px",
                        }}
                        color="primary"
                        count={totalPages}
                        page={pageNumber}
                        onChange={(_, num) => {
                            setPageNumber(num);
                        }}
                    />
                }

            </div>

        </div>
    );

}

export default Publications;
