import {useEffect} from "react";
import axios from "axios";
import Header from "../components/headers/Header";
import Footer from "../components/footers/Footer";
import SubjectItemList from "../components/items/SubjectItemList";

const url = axios.create({
    baseURL: 'http://localhost:8080/v1/'
});

const Subject = ({course, subjects, setSubjects, selectedSubject, setSelectedSubject}) => {

    useEffect(() => {
        const get_subjects = () => {
            return url
                .get('/subjects', {params : {course : course}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                });
        }

        const get_data = async () => {
            let subjects = await get_subjects();

            let list = [];
            for (let i = 0; i < subjects.length; i++) {
                let id = subjects[i].id;
                let name = subjects[i].name;

                list.push(
                    {
                        id: id,
                        name : name
                    }
                );
            }

            setSubjects(list);
            console.log(list);
        }
        get_data().catch(console.error);

    }, [course, setSubjects])

    return (
        <div>
            <Header/>
            <SubjectItemList subjects={subjects} selectedSubject={selectedSubject} setSelectedSubject={setSelectedSubject}/>
            <Footer/>
        </div>
    )
}

export default Subject;
