import {useEffect, useState} from "react";
import axios from "../config/axios";
import Header from "../components/headers/Header";
import PublicationItemList from "../components/items/PublicationItemList";
import {Pagination} from "@mui/material";


const Publications = ({subjectTopicId, publications, setPublications, selectedPublication, setSelectedPublication}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        const getPublications = () => {
            return axios
                .get('/publications', {params: {subjectTopicId: subjectTopicId, pageNumber: pageNumber, pageSize: pageSize}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        }

        const getData = async () => {
            let publications = await getPublications();
            let remainder = publications.totalPages % pageSize;
            let extra = 0;
            if (remainder !== 0 && publications.totalPages !== 1) {
                extra = 1;
            }

            setTotalPages(Math.ceil(publications.totalPages / pageSize) + extra);

            let list = [];
            for (let i = 0; i < publications.content.length; i++) {
                console.log(publications.content[i]);
                let id = publications.content[i].id;
                let description = publications.content[i].title;
                list.push(
                    {
                        id: id,
                        title: description
                    }
                );
            }

            setPublications(list);
            console.log(list);
        }

        getData().catch(console.error);

    }, [pageNumber, pageSize]);

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <PublicationItemList publications={publications}
                                     selectedPublication={selectedPublication}
                                     setSelectedPublication={setSelectedPublication}
                />

                {!!totalPages &&
                    <Pagination
                        sx={{
                            display: 'flex',
                            justifyContent: 'center',
                            marginX: 'auto',
                        }}
                        style={{
                            width: "100%",
                            position: "absolute",
                            bottom: "5px",
                        }}
                        color="primary"
                        count={totalPages}
                        page={pageNumber}
                        onChange={(_, num) => {
                            setPageNumber(num);
                        }}
                        />
                }

            </div>

        </div>
    );

}

export default Publications;
