import "./Form.css"
import {useState} from "react";
import axios from "axios";
import TopicSelector from "../selects/TopicSelector";

const SubjectForm = ({topics, selectedTopic, setSelectedTopic, course, subjectId}) => {

    const [topicTitle, setTopicTitle] = useState('');

    const url = axios.create({
        baseURL: 'http://localhost:8080/v1/',
    });

    const handleTopicTitle = (event) => {
        event.preventDefault();
        setTopicTitle(event.target.value);
    }

    const createNewSubject = (event) => {
        event.preventDefault();
        console.log(topicTitle);

        return url
            .post('/subject-topics', {course_number: course, subject_id: subjectId, topic_id: 1})
            .then((response) => {
                return response.data;
            })
            .catch((error) => {
                return error;
            });

    }

    return (
        <div className="form min-w-[900px] max-w-[80vw] mx-auto flex justify-center">
            <form>
                <div className=" mt-[200px]">
                    <TopicSelector courses={topics} selectedCourse={selectedTopic}
                                   handleSelectCourse={setSelectedTopic}/>
                    {/*<Button text="Показать предметы" handleButton={handleButton}/>*/}
                </div>
            </form>
        </div>
    )
}

export default SubjectForm;
