import {BrowserRouter, Route, Routes} from 'react-router-dom';

// pages
import About from './pages/About';
import NotFound from './pages/NotFound';
import Course from "./pages/Course";
import {useState} from "react";
import Subject from "./pages/Subject";


const App = () => {
    const [courses, setCourses] = useState([]);
    const [selectedCourse, setSelectedCourse] = useState(1);

    const [subjects, setSubjects] = useState([]);

    const handleSelectCourse = (value) => {
        setSelectedCourse(parseInt(value));
    }

    return (
        <div>
            <BrowserRouter>
                <Routes>
                    <Route exact path="/"
                           element={<Course title="Платформа для студентов" note="Все права защищены (нет)" courses={courses} selectedCourse={selectedCourse} handleSelectCourse={handleSelectCourse} setCourses={setCourses}/>}
                    />
                    <Route exact path="/subjects"
                           element={<Subject course={selectedCourse} subjects={subjects} setSubjects={setSubjects}/>}
                    />
                    <Route exact path="/about" element={<About/>}
                    />
                    <Route path="*" element={<NotFound/>}
                    />
                </Routes>
            </BrowserRouter>
        </div>
    );
}

export default App;
