const Publication = () => {
    return (

        <div className="inset-0 overflow-y-auto">
            <div className="flex items-center justify-center min-h-screen">
                <div className="bg-white p-8 h-[20vh] w-[60vh] overflow-auto">

                    <h1 className="text-2xl">Заголовок публикации</h1>
                    <div className="max-h-[75%] overflow-auto">
                        <span className="text-lg pr-5 text-justify">Конспекты по математике</span>
                    </div>


                        <div className="border-amber-300 border-2 mt-2 mb-5">
                            <div className="flex justify-between">
                                <h2>Комментарии</h2>
                            </div>
                        </div>

                </div>
            </div>
        </div>
    );
}

export default Publication;
