import axios from "../config/axios";
import {useEffect, useState} from "react";
import Header from "../components/headers/Header";
import SubjectTopicForm from "../components/form/SubjectTopicForm";
import SubjectTopicList from "../components/items/SubjectTopicList";
import {useNavigate} from "react-router-dom";

const SubjectTopic = ({topics, setTopics, topicType, setSelectedTopicType, course, subjectId, allTopics, setAllTopics, subjectTopicId, setSubjectTopicId}) => {

    const [pageNumber, setPageNumber] = useState(0);
    const [pageSize, setPageSize] = useState(3);
    const [totalPages, setTotalPages] = useState(10);

    const navigate = useNavigate();

    const handleNewTopic = (id, topicId) => {
        setSubjectTopicId(id);
        setSelectedTopicType(topicId);
        navigate('/publications')
    }

    useEffect(() => {

        const getTopics = () => {
            return axios
                .get('/subject-topics', {params: {pageNumber: pageNumber, pageSize: pageSize, subjectId: subjectId}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        }

        const getAllTopics = () => {
            return axios
                .get('/topics')
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                })
        };

        const getDataAllTopics = async () => {
            let allTopics = await getAllTopics();
            setAllTopics(allTopics);
        }

        const getData = async () => {
            let topics = await getTopics();
            console.log(topics);
            let list = [];
            for (let i = 0; i < topics.content.length; i++) {
                let subject_topic_id = topics.content[i].id;
                let id = topics.content[i].topic.id;
                let topic_type = topics.content[i].topic.topic_type;
                list.push(
                    {
                        id: subject_topic_id,
                        topic: {
                            id,
                            topic_type
                        }
                    }
                );
            }
            setTopics(list);
        }

        getData().catch(console.error);
        getDataAllTopics().catch(console.error);
    }, [handleNewTopic]);

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <SubjectTopicList topics={topics}
                                  selectedTopic={topicType}
                                  setSelectedTopic={setSelectedTopicType}
                                  setSubjectTopicId={setSubjectTopicId}
                                  selectedSubjectTopicId={subjectTopicId}
                                  handleNewTopic={handleNewTopic}
                />
                <SubjectTopicForm topics={allTopics}
                                  selectedTopic={topicType}
                                  setSelectedTopic={setSelectedTopicType}
                                  course={course}
                                  subjectId={subjectId}/>
            </div>
            {/*<Footer/>*/}
        </div>
    )

}

export default SubjectTopic;
