const CommentItem = ( {username, createdDate, comment, commentId} ) => {
    return (
        <div className="bg-white rounded mt-3 mb-3" key={commentId}>
            <div>
                <div className="flex">
                    {username}
                </div>
                <div className="flex">
                    <div className="flex mt-3 mb-3">
                        {createdDate}
                    </div>
                    {
                        JSON.parse(sessionStorage.getItem('user')).role === 'Администратор' &&
                        <div className="flex mt-3 mb-3 ml-auto mr-2 cursor-pointer">
                            Удалить
                        </div>
                    }
                </div>

            </div>
            <div className="flex">
                {comment}
            </div>

        </div>
    )
}

export default CommentItem;
