import {useEffect, useState} from "react";
import axios from "../config/axios";
import Header from "../components/headers/Header";
import SubjectItemList from "../components/items/SubjectItemList";
import SubjectForm from "../components/form/SubjectForm";
import SubjectItem from "../components/items/SubjectItem";
import {Pagination} from "@mui/material";

const Subject = ({course, subjects, setSubjects, selectedSubject, setSelectedSubject}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [totalPages, setTotalPages] = useState();

    useEffect(() => {
        const getSubjects = () => {
            return axios
                .get('/subjects', {params: {pageNumber: pageNumber - 1, pageSize: pageSize, course: course}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                });
        }

        const getData = async () => {
            let subjects = await getSubjects();
            let newList = [];
            newList.push({id: 1, name: '1'})
            newList.push({id: 2, name: '2'})
            newList.push({id: 3, name: '3'})
            newList.push({id: 4, name: '4'})
            newList.push({id: 5, name: '5'})
            newList.push({id: 6, name: '6'})
            newList.push({id: 7, name: '7'})
            newList.push({id: 8, name: '8'})
            newList.push({id: 9, name: '9'})
            newList.push({id: 10, name: '10'})
            newList.push({id: 11, name: '11'})
            newList.push({id: 12, name: '12'})
            let remainder = newList % pageSize
            setTotalPages(newList / pageSize + remainder > 0 ? 1 : 0);
            // let remainder = subjects.totalPages % pageSize
            // setTotalPages(subjects.totalPages / pageSize + remainder > 0 ? 1 : 0);

            let list = [];
            for (let i = 0; i < subjects.content.length; i++) {
                let id = subjects.content[i].id;
                let name = subjects.content[i].name;

                list.push(
                    {
                        id: id,
                        name: name
                    }
                );
            }
            // setSubjects(list);

            setSubjects(newList);
        }
        getData().catch(console.error);

    }, [course, pageNumber, pageSize, ])

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <SubjectItemList subjects={subjects}
                                 selectedSubject={selectedSubject}
                                 setSelectedSubject={setSelectedSubject}/>
                {!!totalPages &&
                    <Pagination
                        sx={{
                            display: 'flex',
                            justifyContent: 'center',
                            marginX:'auto',
                        }}
                        style={{
                            width: "100%",
                            position: "absolute",
                            bottom: "5px",
                        }} color="primary"
                        count={totalPages + 1}
                        page={pageNumber}
                        onChange={(_, num) => {
                            setPageNumber(num);
                        }}

                    />
                }

                <SubjectForm/>

            </div>
            {/*<Footer/>*/}
        </div>
    )
}

export default Subject;
