import {useEffect, useState} from "react";
import axios from "../config/axios";
import Header from "../components/headers/Header";
import SubjectItemList from "../components/items/SubjectItemList";
import SubjectForm from "../components/form/SubjectForm";
import {Pagination, TablePagination} from "@mui/material";

const Subject = ({course, subjects, setSubjects, selectedSubject, setSelectedSubject}) => {

    const [pageNumber, setPageNumber] = useState(1);
    const [pageSize, setPageSize] = useState(5);
    const [totalPages, setTotalPages] = useState();
    const [totalSize, setTotalSize] = useState();

    useEffect(() => {
        const getSubjects = () => {
            return axios
                .get('/subjects', {params: {pageNumber: pageNumber - 1, pageSize: pageSize, course: course}})
                .then((response) => {
                    return response.data;
                })
                .catch((error) => {
                    return error;
                });
        }

        const getData = async () => {
            let subjects = await getSubjects();
            let remainder = subjects.totalPages % pageSize
            let extra = 0;
            if (remainder !== 0 && subjects.totalPages !== 1) {
                extra = 1;
            }

            setTotalPages(Math.ceil(subjects.totalPages / pageSize) + extra);
            setTotalSize(subjects.totalSize);

            let list = [];
            for (let i = 0; i < subjects.content.length; i++) {
                let id = subjects.content[i].id;
                let name = subjects.content[i].name;

                list.push(
                    {
                        id: id,
                        name: name
                    }
                );
            }
            setSubjects(list);

        }
        getData().catch(console.error);

    }, [course, pageNumber, pageSize,])

    return (
        <div>
            <Header/>
            <div className="mt-[200px]">
                <SubjectItemList subjects={subjects}
                                 selectedSubject={selectedSubject}
                                 setSelectedSubject={setSelectedSubject}/>
                {!!totalPages &&
                    // <Pagination
                    //     sx={{
                    //         display: 'flex',
                    //         justifyContent: 'center',
                    //         marginX: 'auto',
                    //     }}
                    //     style={{
                    //         width: "100%",
                    //         position: "absolute",
                    //         bottom: "5px",
                    //     }} color="primary"
                    //     count={totalPages}
                    //     page={pageNumber}
                    //     onChange={(_, num) => {
                    //         setPageNumber(num);
                    //     }}
                    //
                    // />
                    <TablePagination
                        count={totalSize}
                        page={pageNumber}
                        rowsPerPage={pageSize}
                        onPageChange={(_, num) => {
                            setPageNumber(num);
                        }}
                        onRowsPerPageChange={(_, num) => {
                            setPageSize(num);
                        }}
                        />
                }

                <SubjectForm/>

            </div>
            {/*<Footer/>*/}
        </div>
    )
}

export default Subject;
