import {useEffect, useState} from "react";
import axios from "axios";
import CommentItem from "./CommentItem";
import Button from "../buttons/Button";

const FeedItem = ({title, description, postId}) => {

    const [comments, setComments] = useState([]);
    const [pageNumber, setPageNumber] = useState(1);
    const [fetching, setFetching] = useState(true);
    const [totalCount, setTotalCount] = useState(0);
    const [pageSize, setPageSize] = useState(1);

    useEffect(() => {
        if (fetching) {
            axios.get(`https://jsonplaceholder.typicode.com/comments?postId=${postId}&_limit=${pageSize}`)
                .then(response => {
                    setComments([...comments, ...response.data]);
                    setPageNumber(prevState => prevState + 1);
                    setTotalCount(response.headers['x-total-count'])
                })
                .finally(() => {
                    setFetching(false);
                })
        }
    }, [fetching]);

    const handleMoreComments = () => {
        setFetching(true);
    }

    return (
        <div className="mt-[100px] min-w-[900px] max-w-[80vw] mx-auto justify-center">
            <div className="mx-auto mb-3 justify-items-center w-[80%] overflow-auto">
                <div
                    className="text-center block p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">
                    <h3 className="mb-2 text-5xl font-bold tracking-tight text-gray-900 dark:text-white">{title}</h3>
                    <h6 className="mb-2 text-2xl tracking-tight text-gray-900 dark:text-white">{description}</h6>
                    <h6 className="mb-2 text-2xl tracking-tight text-gray-900 dark:text-white">Дата
                        создания: </h6>
                    <div>
                        <ul>
                            Файлы
                            {/*{*/}
                            {/*    files.map((file) => {*/}
                            {/*        return (*/}
                            {/*            <li key={file.id}*/}
                            {/*                className="cursor-pointer hover:bg-sky-300 text-2 tracking-tight text-gray-900 dark:text-white"*/}
                            {/*                onClick={() => downloadFile(file)}>{file.initial_file_name}</li>*/}
                            {/*        )*/}
                            {/*    })*/}
                            {/*}*/}
                        </ul>
                    </div>
                    {
                        comments.map((comment) => {
                            return (
                                <CommentItem key={comment.id}
                                             comment={comment.body}
                                             username={comment.name}
                                             commentId={comment.id}
                                />
                            )
                        })
                    }
                    {
                        comments.length !== totalCount &&
                        <Button text="Загрузить еще" handleButton={handleMoreComments}/>
                    }
                </div>
            </div>
        </div>
    )
}

export default FeedItem;
