                    <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{publicationTitle}</h5>
                    <h5 className="text-xs dark:text-white">Автор поста - {authorName}</h5>
                </div>
            </div>
            <div>
                hello
            </div>
        </div>

    )

}

