import React from "react";

const Header = ({email}) => {
    return (
        <div className="flex-col">
            <header
                className="flex justify-center sticky z-50  hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700 text-white top-0 p-4 text-xl">
                Платформа для студентов
                {email !== '' &&
                    <div className="flex justify-center">
                        {email}
                    </div>
                }
            </header>
        </div>
    );
}

export default Header;
