import {useNavigate} from "react-router-dom";
import TopicItem from "./TopicItem";
import {useEffect} from "react";

const SubjectTopicList = ({topics, setSelectedTopic, selectedSubjectTopicId, setSubjectTopicId}) => {
    const navigate = useNavigate();

    const handleSelectedTopic = (id) => {
        setSelectedTopic(id);
        console.log("Выбрали топик с id " + id);
        // navigate('/publications')
    }

    const setSelectedSubjectTopicId = (id) => {
        setSubjectTopicId(id);
        console.log("выбранный топик id " + id);
    }

    useEffect(() => {
        console.log("FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF");
        console.log(topics);
    }, []);

    return (
        <div className="list min-w-[900px] max-w-[80vw] mx-auto flex justify-center">
            <ul>
                {
                    topics.map((topic) => {
                        // console.log(topic);
                        return (
                            <TopicItem key={topic.topic.id}
                                       topicId={topic.topic.id}
                                       topicName={topic.topic.topic_type}
                                       handleClick={handleSelectedTopic}
                                       onClick={() => {
                                           setSelectedSubjectTopicId(topic.id);
                                           // handleSelectedTopic(topic.topic.id);
                                       }}>
                            </TopicItem>

                        )
                    })
                }
            </ul>
        </div>
    )
}

export default SubjectTopicList;
