import Button from "../buttons/Button";
import {useEffect, useState} from "react";
import axios from "../../config/axios";

const SubjectItem = ({ subjectId, subjectName, handleClick }) => {

    const [isAdmin, setIsAdmin] = useState(false);

    useEffect(() => {
        if (JSON.parse(sessionStorage.getItem('user')) === null) {
            setIsAdmin(false);
        } else if (JSON.parse(sessionStorage.getItem('user')).role === 'Администратор') {
            setIsAdmin(true);
        }
    }, []);

    const handle = () => {
        handleClick(subjectId);
    }

    const deleteSubject = () => {
        return axios
            .delete(`/subjects/${subjectId}`, {
                headers: {
                    "Access-Control-Allow-Origin": "*"
                },
                withCredentials: true
            })
            .then((response) => {
                return response.data;
            })
            .catch((error) => {
                return error.response;
            })
    }

    return (
        <div className="cursor-pointer mx-auto mb-3 justify-items-center w-[80%]" key={subjectId}>
            <div className="text-center block p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700" onClick={handle}>
                <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{subjectName}</h5>
            </div>
            {
                isAdmin &&
                <div className="text-white ml-auto mb-auto w-[10%] cursor-pointer">
                    <div className="bg-gray-50 text-black rounded" onClick={deleteSubject}>
                        Удалить
                    </div>
                </div>
            }
        </div>
    )
}

export default SubjectItem;
