import Header from "../components/headers/Header";
import Button from "../components/buttons/Button";
import {useNavigate} from "react-router-dom";
import FeedItem from "../components/items/FeedItem";
import {useEffect, useState} from "react";
import axios from "axios";

const Feed = () => {

    const navigate = useNavigate();

    const [posts, setPosts] = useState([]);
    const [pageNumber, setPageNumber] = useState(1);
    const [fetching, setFetching] = useState(true);
    const [totalCount, setTotalCount] = useState(0);

    useEffect(() => {
        if (fetching) {
            axios.get(`https://jsonplaceholder.typicode.com/posts?_limit=10&_page=${pageNumber}`)
                .then(response => {
                    setPosts([...posts, ...response.data]);
                    setPageNumber(prevState => prevState + 1);
                    setTotalCount(parseInt(response.headers['x-total-count']));
                })
                .finally(() => {
                    setFetching(false);
                })
        }
    }, [fetching])

    useEffect(() => {
        document.addEventListener('scroll', scrollHandler)
        return () => {
            document.removeEventListener('scroll', scrollHandler);
        }
    }, []);

    const scrollHandler = (e) => {
        if (e.target.documentElement.scrollHeight - (e.target.documentElement.scrollTop + window.innerHeight) < 100
            && posts.length === totalCount
        ) {
            setFetching(true);
        }
    }

    const goToCourses = () => {
        navigate('/courses');
    }

    return (
        <div>
            <Header/>
            <div className="flex-col ml-[85%]">
                <Button text="Найти нужный материал" handleButton={goToCourses}/>
            </div>
            {
                (sessionStorage.getItem('user') !== null) &&
                <Button text="Создать новую публикацию" handleButton={handleButton}/>
            }
            {
                posts.map((post) => {
                    return (
                        <FeedItem key={post.id}
                                  title={post.title}
                                  description={post.body}
                                  postId={post.id}
                        />
                    )
                })
            }
        </div>
    )
}

export default Feed;
